mhq.StartUp = function(eventCode, addOnName)	if addOnName == mhq.AddonID then		mhq.Ready = true				SLASH_COMMANDS["/mhq"] = mhq.CommandLine		SLASH_COMMANDS["/mhqa"] = mhq.Abandon		SLASH_COMMANDS["/mhqbl"] = mhq.BlackList		SLASH_COMMANDS["/mhqs"] = mhq.Share		SLASH_COMMANDS["/mhqwl"] = mhq.WhiteList		SLASH_COMMANDS["/mhqt"] = mhq.Track				mhq.QuestsToAbandon = ZO_SavedVars:New(mhq.AddonID, 1, "BlackList", {}, "MHQ") or {}		mhq.QuestsToShare = ZO_SavedVars:New(mhq.AddonID, 1, "WhiteList", {}, "MHQ") or {}		mhq.SetToAlwaysShare = ZO_SavedVars:New(mhq.AddonID, 1, "AlwaysShare", {}, "MHQ") or {}		mhq.SetToAlwaysTrack = ZO_SavedVars:New(mhq.AddonID, 1, "AlwaysTrack", {}, "MHQ") or {}				d(mhq.AddonDescr.." loaded")		d("... FOR COMMAND INFORMATION: /mhq help")	endend