mhw.CommandLine = function(text)	if not mhw.Ready then return; end	if not text or text == "" or text == "help" then		d(mhw.AddonName.." accepted commands:")		d(mhw.Prefix.Default.." /"..mhw.AddonAbbrev.."  << shows help")		d(mhw.Prefix.Default.." /"..mhw.AddonAbbrev.." acct @name  << sets account name")		d(mhw.Prefix.Default.." /"..mhw.AddonAbbrev.." coms url  << sets coms information")		d(mhw.Prefix.Default.." /war %% << sends %% text to Zone and Party chat")		d(mhw.Prefix.Default.." /warcommand << toggles command, if group lead")		d(mhw.Prefix.Default.." /spamcoms << toggles coms spam (commander only)")		d(mhw.Prefix.Default.." /coms << puts coms into Party chat")		d(mhw.Prefix.Default.." /warcoms << puts coms into Party & Zone")		d(mhw.Prefix.Default.." /about << puts About into Party chat")		d(mhw.Prefix.Default.." /warabout << puts About into Party & Zone")		return	end		if string.find(text, "acct") then		local t = text:gsub("acct ","")		mhw.SavedVars.opAccount[1] = t		mhw.op.ACCT = t	end		if string.find(text, "coms") then		local t = text:gsub("coms ","")		mhw.SavedVars.opComms[1] = t		mhw.op.COMS = t	endend